import { Router } from "express";
import { methods as indexController } from "../controllers/indexController";

const router = Router();

/*======================== EMPLEADOS ========================*/
//[GET]
router.get('/', indexController.getEmpleados);
//[GET:id]
router.get('/:id', indexController.getEmpleado);
//[POST]
router.post('/', indexController.addEmpleado);
//[PUT:id]
router.put('/:id', indexController.putEmpleado);
//[DELETE:id]
router.delete('/:id', indexController.deleteEmpleado);


export default router;

